<?php mesmerize_get_header(); ?>    <div class="content blog-page">        <div class="<?php mesmerize_page_content_wrapper_class(); ?>">            <div class="row">                <div class="col-xs-12 <?php echo(is_active_sidebar('sidebar-1') ? 'col-sm-9' : 'col-sm-12'); ?>">                    <div class="post-list row">                        <?php                        if (have_posts()):                            while (have_posts()):                                the_post();                                get_template_part('template-parts/content', get_post_format());                            endwhile;                        else:                            get_template_part('template-parts/content', 'none');                        endif;                        ?>                    </div>                    <div class="navigation-c">                        <?php                        if (have_posts()):                            mesmerize_print_pagination(array(                                'prev_text'          => __('<i class="fa fa-angle-left" aria-hidden="true"></i>', 'mesmerize'),                                'next_text'          => __('<i class="fa fa-angle-right" aria-hidden="true"></i>', 'mesmerize'),                                'before_page_number' => '<span class="meta-nav screen-reader-text">' . __('Page', 'mesmerize') . ' </span>',                            ));                        endif;                        ?>                    </div>                </div>                <?php get_sidebar(); ?>            </div>        </div>    </div><?php mesmerize_get_footer();