<div id="post-<?php the_ID(); ?>"<?php post_class(); ?>>    <div class="post-content-single">        <h1><?php the_title(); ?></h1>		<?php get_template_part( 'template-parts/content-post-single-header' ) ?>        <div class="post-content-inner">			<?php			if ( has_post_thumbnail() ) {				the_post_thumbnail();			}			the_content();			wp_link_pages( array(				'before'      => '<div class="page-links"><span class="page-links-title">' . __( 'Pages:', 'mesmerize' ) . '</span>',				'after'       => '</div>',				'link_before' => '<span>',				'link_after'  => '</span>',				'pagelink'    => '<span class="screen-reader-text">' . __( 'Page', 'mesmerize' ) . ' </span>%',				'separator'   => '<span class="screen-reader-text">, </span>',			) );			?>        </div>		<?php echo get_the_tag_list( '<p class="tags-list"><i data-cp-fa="true" class="font-icon-25 fa fa-tags"></i>&nbsp;', ' ', '</p>' ); ?>    </div>	<?php	the_post_navigation( array(		'next_text' => '<span class="meta-nav" aria-hidden="true">' . __( 'Next:', 'mesmerize' ) . '</span> ' .		               '<span class="screen-reader-text">' . __( 'Next post:', 'mesmerize' ) . '</span> ' .		               '<span class="post-title">%title</span><i class="font-icon-post fa fa-angle-double-right"></i>',		'prev_text' => '<i class="font-icon-post fa fa-angle-double-left"></i>' .					   '<span class="meta-nav" aria-hidden="true">' . __( 'Previous:', 'mesmerize' ) . '</span> ' .		               '<span class="screen-reader-text">' . __( 'Previous post:', 'mesmerize' ) . '</span> ' .		               '<span class="post-title">%title</span>',	) );	?>	<?php	if ( comments_open() || get_comments_number() ):		comments_template();	endif;	?></div>